    let gcd = Math.abs(coeff_x, coeff_y);

    if (gcd !== 1) {
        if (coeff_y > 0) {
            coeff_y = Math.floor(coeff_y / gcd);
        }
        if (coeff_x > 0) {
            coeff_x = Math.floor(coeff_x / gcd);
        }
        if (constant > 0) {
            constant = Math.floor(constant / gcd);
