function sector_area(max_radius=49, max_angle=359) {
    let r = Math.floor(Math.random() * max_radius) + 1;
    let a = Math.floor(Math.random() * max_angle) + 1;
    let secArea = parseFloat((a / 360) * Math.PI * r * r);
    let formatted_float = secArea.toFixed(2);

    let problem = `What is the area of a sector with radius ${r} and angle ${a} degrees?`;
    let solution = `${formatted_float}`;
    return [problem, solution];
}
