    constructor(buf, offset, chunk, parent, length = null) {
        super(buf, offset, chunk, parent, length);
        if (this._length === null) {
            this.declareField("dword", "size", 0x0);
            this.declareField("binary", "binary", {length: this.size()});
        } else {
