function decimal_to_bcd(max_number=10000) {
    let n = user_randint(1000, max_number);
    let x = n;
    // let binstring = '';
    let bcdstring = '';
    while (x > 0) {
        let nibble = x % 16;
        bcdstring = nibble.toString() + bcdstring;
        x >>= 4;
    }

    let problem = `BCD of Decimal Number &${n} = &`;
    return [problem, `&${bcdstring}&`];
}
