function volume_pyramid(max_length=20, max_width=20, max_height=50, unit='m') {
    let length = user_randint(1, max_length);
    let width = user_randint(1, max_width);
    let height = user_randint(1, max_height);

    let ans = parseFloat(((length * width * height) / 3).toFixed(2));

    let problem = `Volume of pyramid with base length = ${length} ${unit}, base width = ${width} ${unit} and height = ${height} ${unit} is`;
    let solution = `${ans} ${unit}^3`;
    return [problem, solution];
}
