function area_of_circle_given_center_and_point(max_coordinate=10, max_radius=10) {
    let r = user_randint(0, max_radius);
    let center_x = user_randint(-max_coordinate, max_coordinate);
    let center_y = user_randint(-max_coordinate, max_coordinate);

    let angles = [0, Math.PI / 6, Math.PI / 2, Math.PI, Math.PI + Math.PI / 6, 3 * Math.PI / 2];
    let angle = user_choice_func2(angles);

    let point_x = center_x + Math.round(r * Math.cos(angle) * 100) / 100;
    let point_y = center_y + Math.round(r * Math.sin(angle) * 100) / 100;

    let area = Math.round(Math.PI * r * r * 100) / 100;

    let problem = `Area of circle with center (${center_x},${center_y}) and passing through (${point_x}, ${point_y}) is`;
    return [problem, `${area}`];
}
