function _siftup_max(heap, pos) {
    let endpos = heap.length;
    let startpos = pos;
    let newitem = heap[pos];
    let childpos = 2 * pos + 1;
    while (childpos < endpos) {
        let rightpos = childpos + 1;
        if (rightpos < endpos && !(heap[rightpos] < heap[childpos])) {
            childpos = rightpos;
