function curved_surface_area_cylinder(max_radius=49, max_height=99) {
    let r = Math.floor(Math.random() * max_radius) + 1;
    let h = Math.floor(Math.random() * max_height) + 1;
    let csa = 2 * Math.PI * r * h;
    let formatted_float = csa.toFixed(2);

    let problem = `What is the curved surface area of a cylinder of radius, ${r} and height, ${h}?`;
    let solution = `${formatted_float}`;
    return [problem, solution];
}
