    var j = user_randint(0, 3);
    var lst = user_sample_func1(Array.from({length: 100}, (_, i) => 200 + i), n);
    var lst_per = [80, 90, 95, 99];
    var lst_t = [1.282, 1.645, 1.960, 2.576];
    var mean = 0;
    var sd = 0;
    for (var i = 0; i < lst.length; i++) {
        var count = lst[i] + mean;
        mean = count;
    }
    mean = mean / n;
    for (var i = 0; i < lst.length; i++) {
        var x = Math.pow(lst[i] - mean, 2) + sd;
        sd = x;
    }
    sd = sd / n;
    var standard_error = lst_t[j] * Math.sqrt(sd / n);
    var upper = Math.round(mean + standard_error, 2);
    var lower = Math.round(mean - standard_error, 2);
    var problem = 'The confidence interval for sample ' + lst.join(', ') + ' with ' + lst_per[j] + '% confidence is';
    var solution = '(' + upper + ', ' + lower + ')';
    return [problem, solution];

