                    if (retval && retval.slice(-2) !== "\n\n") {
                        retval += "\n";
                    }
                    retval += "[" + section + "]\n";
                    if (addtoretval) {
                        retval += addtoretval;
                    }
                }
                for (var s in addtosections) {
                    if (addtosections.hasOwnProperty(s)) {
                        newsections[section + "." + s] = addtosections[s];
                    }
                }
            }
        }
        sections = newsections;
    }
    return retval;
