function rgb_to_hls(r, g, b) {
    let maxc = Math.max(r, g, b);
    let minc = Math.min(r, g, b);
    let sumc = maxc + minc;
    let rangec = maxc - minc;
    let l = sumc / 2.0;
    if (minc === maxc) {
        return [0.0, l, 0.0];
    }
    let s;
    if (l <= 0.5) {
        s = rangec / sumc;
    } else {
        s = rangec / (2.0 - sumc);
    }
    let rc = (maxc - r) / rangec;
    let gc = (maxc - g) / rangec;
    let bc = (maxc - b) / rangec;
    let h;
    if (r === maxc) {
        h = bc - gc;
    } else if (g === maxc) {
        h = 2.0 + rc - bc;
    } else {
        h = 4.0 + gc - rc;
    }
    h = (h / 6.0) % 1.0;
    return [h, l, s];
}
