function nsmallest(n, iterable) {
    if (n === 1) {
        let it = iterable[Symbol.iterator]();
        let result = Math.min(...it);
        return Number.isFinite(result) ? [result] : [];
    }

    let size;
    try {
        size = iterable.length;
    } catch (e) {
        size = undefined;
    }

    if (size !== undefined && n >= size) {
        return [...iterable].sort().slice(0, n);
    }

    let it = iterable[Symbol.iterator]();
    let result = [];
    for (let i = 0; i < n; i++) {
        let elem = it.next().value;
        if (elem === undefined) break;
        result.push([elem, i]);
    }
    if (result.length === 0) {
        return result;
    }
    result.sort((a, b) => b[0] - a[0]); // Max-heap
    let top = result[0][0];
    let order = n;
    for (let elem of it) {
        if (elem < top) {
            result.push([elem, order]);
            result.sort((a, b) => b[0] - a[0]);
            result.pop();
            top = result[0][0];
            order++;
        }
    }
    result.sort((a, b) => a[1] - b[1]);
    return result.map(x => x[0]);
}
